import React from "react";
import {
  ScrollView,
  StyleSheet,
  Image,
  TouchableWithoutFeedback,
  ImageBackground,
  Dimensions
} from "react-native";
//galio
import { Block, Text, theme } from "galio-framework";
//argon
import { Images, argonTheme } from "../constants/";

const { width } = Dimensions.get("screen");

const thumbMeasure = (width - 48 - 32) / 3;
const cardWidth = width - theme.SIZES.BASE * 2;

const about = "Mussum Ipsum, cacilds vidis litro abertis. Mais vale um bebadis conhecidiss, que um alcoolatra anonimis. Quem num gosta di mé, boa gentis num é. Quem manda na minha terra sou euzis! Copo furadis é disculpa de bebadis, arcu quam euismod magna.";

class Empresa extends React.Component {
  render() {
    return (
      <Block flex center>
        <ScrollView
          showsVerticalScrollIndicator={true}
          contentContainerStyle={styles.articles}>
          <Block center>
            <Image source={Images.LogoOnboarding} style={styles.logo} />
            <Text style={styles.about}>{about}</Text>
          </Block>
        </ScrollView>
      </Block>
    );
  }
}

const styles = StyleSheet.create({
  home: {
    width: width,    
  },
  articles: {
    width: width - theme.SIZES.BASE * 2,
    paddingVertical: theme.SIZES.BASE,
  },
  about: {
    color: theme.COLORS.GREY
  }
});

export default Empresa;
